wp.customize.controlConstructor["xirki-multicheck"]=wp.customize.xirkiDynamicControl.extend({initXirkiControl:function(){var control=this;control.container.on("change","input",function(){var value=[],i=0;jQuery.each(control.params.choices,function(key){if(control.container.find('input[value="'+key+'"]').is(":checked")){control.container.find('input[value="'+key+'"]').parent().addClass("checked");value[i]=key;i++}else{control.container.find('input[value="'+key+'"]').parent().removeClass("checked")}});control.setting.set(value)})}});