wp.customize.controlConstructor["xirki-multicolor"]=wp.customize.Control.extend({ready:function(){"use strict";var control=this;if(!_.isUndefined(window.xirkiControlLoader)&&_.isFunction(xirkiControlLoader)){xirkiControlLoader(control)}else{control.initXirkiControl()}},initXirkiControl:function(){"use strict";var control=this,colors=control.params.choices,keys=Object.keys(colors),value=this.params.value,i=0;function xirkiMulticolorChangeHandler(control,value,subSetting){var picker=control.container.find(".multicolor-index-"+subSetting),args={change:function(){setTimeout(function(){control.saveValue(subSetting,picker.val());control.container.find(".multicolor-index-"+subSetting).trigger("change")},100)}};if(_.isObject(colors.irisArgs)){_.each(colors.irisArgs,function(irisValue,irisKey){args[irisKey]=irisValue})}picker.wpColorPicker(args)}while(i<Object.keys(colors).length){xirkiMulticolorChangeHandler(this,value,keys[i]);i++}},saveValue:function(property,value){var control=this,input=control.container.find(".multicolor-hidden-value"),val=control.setting._value;val[property]=value;jQuery(input).attr("value",JSON.stringify(val)).trigger("change");control.setting.set(val)}});