wp.customize.controlConstructor["xirki-slider"]=wp.customize.xirkiDynamicControl.extend({initXirkiControl:function(){var control=this,changeAction="postMessage"===control.setting.transport?"mousemove change":"change",rangeInput=control.container.find('input[type="range"]'),textInput=control.container.find('input[type="text"]'),value=control.setting._value;textInput.attr("value",value);rangeInput.on("mousemove change",function(){textInput.attr("value",rangeInput.val())});rangeInput.on(changeAction,function(){control.setting.set(rangeInput.val())});textInput.on("input paste change",function(){rangeInput.attr("value",textInput.val());control.setting.set(textInput.val())});control.container.find(".slider-reset").on("click",function(){textInput.attr("value",control.params.default);rangeInput.attr("value",control.params.default);control.setting.set(textInput.val())})}});