wp.customize.controlConstructor["xirki-sortable"]=wp.customize.Control.extend({ready:function(){"use strict";var control=this;jQuery(control.container.find("ul.sortable").first()).sortable({update:function(){control.setting.set(control.getNewVal())}}).disableSelection().find("li").each(function(){jQuery(this).find("i.visibility").click(function(){jQuery(this).toggleClass("dashicons-visibility-faint").parents("li:eq(0)").toggleClass("invisible")})}).click(function(){control.setting.set(control.getNewVal())})},getNewVal:function(){var items=jQuery(this.container.find("li")),newVal=[];_.each(items,function(item){if(!jQuery(item).hasClass("invisible")){newVal.push(jQuery(item).data("value"))}});return newVal}});