jQuery(document).ready(function(){wp.customize.control.each(function(control){if(control.params&&control.params.preset&&!_.isEmpty(control.params.preset)){wp.customize(control.id,function(value){value.bind(function(to){_.each(control.params.preset,function(preset,valueToListen){if(valueToListen===to){_.each(preset.settings,function(controlValue,controlID){xirkiSetSettingValue.set(controlID,controlValue)})}})})})}})});