goog.provide("webfont.CssClassName");webfont.CssClassName=function(opt_joinChar){this.joinChar_=opt_joinChar||webfont.CssClassName.DEFAULT_JOIN_CHAR};webfont.CssClassName.DEFAULT_JOIN_CHAR="-";goog.scope(function(){var CssClassName=webfont.CssClassName;CssClassName.prototype.sanitize=function(name){return name.replace(/[\W_]+/g,"").toLowerCase()};CssClassName.prototype.build=function(var_args){var parts=[];for(var i=0;i<arguments.length;i++){parts.push(this.sanitize(arguments[i]))}return parts.join(this.joinChar_)}});