goog.provide("webfont.Font");webfont.Font=function(name,opt_variation){this.name_=name;this.weight_=4;this.style_="n";var variation=opt_variation||"n4",match=variation.match(/^([nio])([1-9])$/i);if(match){this.style_=match[1];this.weight_=parseInt(match[2],10)}};goog.scope(function(){var Font=webfont.Font;Font.prototype.getName=function(){return this.name_};Font.prototype.getCssName=function(){return this.quote_(this.name_)};Font.prototype.toCssString=function(){return this.getCssStyle()+" "+this.getCssWeight()+" 300px "+this.getCssName()};Font.prototype.quote_=function(name){var quoted=[];var split=name.split(/,\s*/);for(var i=0;i<split.length;i++){var part=split[i].replace(/['"]/g,"");if(part.indexOf(" ")==-1&&!/^\d/.test(part)){quoted.push(part)}else{quoted.push("'"+part+"'")}}return quoted.join(",")};Font.prototype.getVariation=function(){return this.style_+this.weight_};Font.prototype.getCssVariation=function(){return"font-style:"+this.getCssStyle()+";font-weight:"+this.getCssWeight()+";"};Font.prototype.getCssWeight=function(){return this.weight_+"00"};Font.prototype.getCssStyle=function(){var style="normal";if(this.style_==="o"){style="oblique"}else if(this.style_==="i"){style="italic"}return style};Font.parseCssVariation=function(css){var weight=4,style="n",m=null;if(css){m=css.match(/(normal|oblique|italic)/i);if(m&&m[1]){style=m[1].substr(0,1).toLowerCase()}m=css.match(/([1-9]00|normal|bold)/i);if(m&&m[1]){if(/bold/i.test(m[1])){weight=7}else if(/[1-9]00/.test(m[1])){weight=parseInt(m[1].substr(0,1),10)}}}return style+weight}});